mapper.update:
update tbl_usertest set
	u_point = (select u_point from tbl_usertest
			   where u_id = #{u_id}) - #{totalPrice}
where u_id = #{u_id}

update tbl_usertest set
	u_point = (select u_point from tbl_usertest
			   where u_id = #{u_id}) + #{totalPrice}
where u_id = #{u_id}

mapper.select:
select count(*) from tbl_point
where u_id = #{u_id}
and point_code = #{point_code}
